
public class IfElseAndRelationalOperator {
	/*
	 ==  is equal to
	 !=   not equal to
	 <    less than
	 >    greater than
	 <= less than equal to
	 >= greater then equal to 
	 */

	public static void main(String[] args) {
		int number=10;
		// TODO Auto-generated method stub
		if(number==10) {
			System.out.println("yes Number is equal to 10");
		}
		else {
			System.out.println("No Number is not equal to 10;");
		}

	}

}
